import * as React from "react";
import PropTypes from "prop-types";
import WaterfallChartIcon from "@mui/icons-material/WaterfallChart";
import StackedLineChart from "@mui/icons-material/StackedLineChart";
import ErrorOutlineIcon from "@mui/icons-material/ErrorOutline";
import TextSnippetIcon from "@mui/icons-material/TextSnippet";
import { useMaterialUIController } from "contexts/UIContext";

import Box from "@mui/material/Box";
import {
  Card,
  Grid,
  Icon,
  IconButton,
  Tab,
  Tabs,
  Typography,
} from "@mui/material";
import MDBox from "components/MDBox";
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import elasticApmEndpoints from "network/endpoints/elasticApm";
import ReactApexChart from "react-apexcharts";
import { useEffect, useState } from "react";
import Popover from "@mui/material/Popover";

import MDAvatar from "components/MDAvatar";
import MDTypography from "components/MDTypography";
import breakpoints from "assets/theme/base/breakpoints";

// ... (previous imports)
import aws from "../../assets/svgs/aws-icon.svg";
import lambda from "../../assets/svgs/lambda.svg";
import MDButton from "components/MDButton";
import setupMl from "./actions/setupMl";
import getServiceLogs from "./actions/getServiceLogs";
import Logs from "./Logs";

import SetupMlDialog from "./AnomalyDetection/SetupMlDialog";
import TransactionsChart from "./Transactions/TransactionChart";
import useLastPartOfUrl from "hooks/useLastPartOfUrl";
import Errors from "./Errors";

function CustomTabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <Box
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      sx={{ pt: 1 }}
      {...other}
    >
      {value === index && <Box sx={{ p: 2 }}>{children}</Box>}
    </Box>
  );
}

CustomTabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.number.isRequired,
  value: PropTypes.number.isRequired,
};

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  };
}

const ElasticDashboard = () => {
  const [value, setValue] = useState(0);
  const lastPartOfUrl = useLastPartOfUrl();
  const [tabsOrientation, setTabsOrientation] = useState("horizontal");
  const [dataState, setState] = useState(null); // elastic apm data state
  const handleChange = (event, newValue) => {
    setValue(newValue);
  };
  const [isVisible, setIsVisible] = useState(false);
  const handleClose = () => {
    setIsVisible(false);
  };
  const handleOpen = () => {
    setIsVisible(true);
  };
  const [anchorEl, setAnchorEl] = useState(null);
  const handleMlSetup = async () => {
    handleOpen();
    const { data } = await setupMl();
  };
  const handlePopoverOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handlePopoverClose = () => {
    setAnchorEl(null);
  };

  const open = Boolean(anchorEl);

  const [controller, dispatch] = useMaterialUIController();

  useEffect(() => {
    // A function that sets the orientation state of the tabs.
    function handleTabsOrientation() {
      return window.innerWidth < breakpoints.values.sm
        ? setTabsOrientation("vertical")
        : setTabsOrientation("horizontal");
    }

    /** 
     The event listener that's calling the handleTabsOrientation function when resizing the window.
    */
    window.addEventListener("resize", handleTabsOrientation);

    // Call the handleTabsOrientation function to set the state with the initial value.
    handleTabsOrientation();

    // Remove event listener on cleanup
    return () => window.removeEventListener("resize", handleTabsOrientation);
  }, [tabsOrientation]);

  useEffect(() => {
    const fetchMetrics = async () => {
      console.log("LAST PART OF URL: ", lastPartOfUrl);
      const { data } = await elasticApmEndpoints.getMetrics(lastPartOfUrl);
      setState(data);
      // const logs = await getServiceLogs(lastPartOfUrl);
      // console.log("LOGS: ");
      // console.log(logs);
    };

    fetchMetrics();
  }, [lastPartOfUrl]);

  const { darkMode } = controller;

  const renderSparklineChart = (data, title, yaxisLabel) => {
    const seriesData = data.map((entry, index, array) => {
      const isMemoryUsage = title === "Memory Usage";

      const getValue = () => {
        if (isMemoryUsage) {
          const prev = array[index - 1]
            ? array[index - 1].freeMemory
            : entry.freeMemory;
          const next = array[index + 1]
            ? array[index + 1].freeMemory
            : entry.freeMemory;
          return (prev + entry.freeMemory + next) / 3;
        } else {
          return entry.duration || entry.billedDuration || 0;
        }
      };

      return {
        x: new Date(entry.timestamp).getTime() || "N/A",
        y: getValue(),
        formattedDate: new Date(entry.timestamp).toLocaleTimeString([], {
          hour: "2-digit",
          minute: "2-digit",
          second: "2-digit",
        }),
      };
    });

    const chartOptions = {
      chart: {
        id: title.toLowerCase().replace(" ", "_"),
        group: "sparklineGroup",
        type: "area",
        height: 200,
      },
      dataLabels: {
        enabled: false,
      },
      xaxis: {
        type: "datetime",
        labels: {
          style: {
            colors: darkMode ? "#FFFFFF" : "#000000",
          },
        },
      },
      yaxis: {
        title: {
          text: yaxisLabel,
          style: {
            color: darkMode ? "#FFFFFF" : "#000000",
          },
        },
        labels: {
          style: {
            colors: darkMode ? "#FFFFFF" : "#000000",
            fontSize: "12px",
            fontWeight: "normal",
          },
          formatter: function (val) {
            if (title === "Billed Duration" || title === "Function Duration") {
              return `${val} ms`;
            } else if (title === "Memory Usage") {
              return `${Math.round(val)} bytes`;
            }
            return val;
          },
        },
      },
      tooltip: {
        x: {
          formatter: function (val) {
            return new Date(val).toLocaleTimeString([], {
              hour: "2-digit",
              minute: "2-digit",
              second: "2-digit",
            });
          },
        },
      },
    };

    return (
      <Card>
        <Typography
          variant="h6"
          gutterBottom
          style={{ color: darkMode ? "#FFFFFF" : "#000000" }}
        >
          {title}
        </Typography>
        <ReactApexChart
          options={chartOptions}
          series={[{ name: title, data: seriesData }]}
          type="area"
          height={200}
        />
      </Card>
    );
  };

  return (
    <DashboardLayout>
      <>
        <MDBox mt={8}>
          <Card
            sx={{
              mt: -8,
              py: 2,
              px: 2,
            }}
          >
            <Grid container spacing={3} alignItems="center">
              <Grid item>
                <MDAvatar alt="profile-image" size="xl" shadow="sm" />
              </Grid>
              <Grid item>
                <MDBox height="100%" mt={0.5} lineHeight={1}>
                  <MDTypography variant="h5" fontWeight="medium">
                    {lastPartOfUrl}
                  </MDTypography>
                  <MDButton onClick={handleMlSetup} variant="text" color="info">
                    SETUP ML
                  </MDButton>
                  <IconButton
                    aria-owns={open ? "mouse-over-popover" : undefined}
                    aria-haspopup="true"
                    onMouseEnter={handlePopoverOpen}
                    onMouseLeave={handlePopoverClose}
                  >
                    <img
                      width="30"
                      height="30"
                      alt=""
                      src="data:image/svg+xml;base64,"
                      class="euiIcon euiButtonIcon__icon css-13e8g5a-euiIcon-l-inherit"
                      aria-hidden="true"
                    ></img>
                  </IconButton>
                  <IconButton
                    aria-owns={open ? "mouse-over-popover" : undefined}
                    aria-haspopup="true"
                    onMouseEnter={handlePopoverOpen}
                    onMouseLeave={handlePopoverClose}
                  >
                    <img width="24" height="24" src={lambda} />
                  </IconButton>
                  <IconButton
                    aria-owns={open ? "mouse-over-popover" : undefined}
                    aria-haspopup="true"
                    onMouseEnter={handlePopoverOpen}
                    onMouseLeave={handlePopoverClose}
                  >
                    <img width="24" height="24" src={aws} />
                  </IconButton>
                  <Popover
                    id="mouse-over-popover"
                    sx={{
                      pointerEvents: "none",
                    }}
                    open={open}
                    anchorEl={anchorEl}
                    anchorOrigin={{
                      vertical: "bottom",
                      horizontal: "left",
                    }}
                    transformOrigin={{
                      vertical: "top",
                      horizontal: "left",
                    }}
                    onClose={handlePopoverClose}
                    disableRestoreFocus
                  >
                    <MDTypography sx={{ p: 1 }}>Service</MDTypography>
                    <MDTypography sx={{ p: 2 }}>Service version</MDTypography>
                  </Popover>
                </MDBox>
              </Grid>

              <Grid item xs={12} md={6} lg={6} sx={{ ml: "auto" }}>
                <Tabs
                  orientation={tabsOrientation}
                  value={value}
                  onChange={handleChange}
                >
                  <Tab
                    {...a11yProps(0)}
                    label="Metrics"
                    icon={
                      <Icon fontSize="small" sx={{ mt: -0.25 }}>
                        <StackedLineChart />
                      </Icon>
                    }
                  />
                  <Tab
                    {...a11yProps(1)}
                    label="Logs"
                    icon={
                      <Icon fontSize="small" sx={{ mt: -0.25 }}>
                        <TextSnippetIcon />
                      </Icon>
                    }
                  ></Tab>
                  <Tab
                    {...a11yProps(2)}
                    label="Transactions"
                    icon={
                      <Icon fontSize="small" sx={{ mt: -0.25 }}>
                        <WaterfallChartIcon />
                      </Icon>
                    }
                  />
                  <Tab
                    {...a11yProps(3)}
                    label="Errors"
                    icon={
                      <Icon fontSize="small" sx={{ mt: -0.25 }}>
                        <ErrorOutlineIcon />
                      </Icon>
                    }
                  />
                </Tabs>
              </Grid>
            </Grid>
          </Card>
        </MDBox>
        <CustomTabPanel value={value} index={0}>
          <Grid container spacing={1}>
            <Grid item xs={12} md={4}>
              {dataState &&
                renderSparklineChart(
                  dataState?.sparklineData?.functionDuration,
                  "Function Duration",
                  "Duration"
                )}
            </Grid>
            <Grid item xs={12} md={4}>
              {dataState &&
                renderSparklineChart(
                  dataState?.sparklineData?.billedDuration,
                  "Billed Duration",
                  "Billed Duration"
                )}
            </Grid>
            <Grid item xs={12} md={4}>
              {dataState &&
                renderSparklineChart(
                  dataState?.sparklineData?.memoryUsage,
                  "Memory Usage",
                  "Free Memory"
                )}
            </Grid>
          </Grid>
        </CustomTabPanel>
        <CustomTabPanel value={value} index={1}>
          <Logs />
        </CustomTabPanel>
        <CustomTabPanel value={value} index={2}>
          <TransactionsChart />
        </CustomTabPanel>
        <CustomTabPanel value={value} index={3}>
          <Errors />
        </CustomTabPanel>
        <SetupMlDialog isVisible={isVisible} handleClose={handleClose} />
      </>
    </DashboardLayout>
  );
};

export default ElasticDashboard;
